import React from "react";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Main from "../Main/Main";
import Movies from "../Movies/Movies";
import SavedMovies from "../SavedMovies/SavedMovies";
import Profile from "../Profile/Profile";
import Register from "../Register/Register";
import Login from "../Login/Login";
import Notfound from "../Notfound/Notfound";
import MainApi from "../../utils/MainApi";
import "../App/App.css";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";
import ProtectedRoute from '../ProtectedRoute/ProtectedRoute';

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [loggedIn, setLoggedIn] = React.useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  function checkToken() {
    const jwt = localStorage.getItem('token');
    MainApi
      .getContent(jwt)
      .then((data) => {
        if (!data) {
          return;
        }
        setLoggedIn(true);
        navigate(location.pathname);
      })
      .catch((err) => {
        setLoggedIn(false);
        console.error(`Ошибка: ${err}`);
      });
  }
  function handleIsLogin() {
    setLoggedIn(true);
  }
  function handlePageBack() {
    navigate(-1);
  }
  return (
    <CurrentUserContext.Provider value={currentUser}>
        <div className="page">
          <Routes>
            <Route path="/" element={<ProtectedRoute element={Main} loggedIn={loggedIn} handleIsLogin={handleIsLogin}/>} />
            <Route path="/movies" element={<Movies handleIsLogin={handleIsLogin} loggedIn={loggedIn}/>} />
            <Route path="/saved-movies" element={<SavedMovies handleIsLogin={handleIsLogin} loggedIn={loggedIn}/>} />
            <Route path="/profile" element={<Profile handleIsLogin={handleIsLogin} loggedIn={loggedIn} />} />
            <Route path="/signup" element={<Register />} />
            <Route path="/signin" element={<Login />} />
            <Route path="*" element={<Notfound  onBack={handlePageBack}/>} />
          </Routes>
        </div>
        </CurrentUserContext.Provider>
  );
}

export default App;
