import React from "react";
import logo from "../../images/logo.svg";
import "../Header/Header.css";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../Navigation/Navigation";
// import { Link } from 'react-router-dom';

function Header({isLogin, handleIsLogin}) {
const location = useLocation();

  return (
    <header className={`header ${location.pathname!=='/' ? 'header_black' : ''}`}>
      <div className="header__container">
        <img
          className="header__logo"
          src={logo}
          alt="белая заглавная буква С в светло-зеленом кругу"
        />
        {!isLogin ? (<ul className="header__menu">
          <li
            type="button"
            aria-label="регистрация"
            className="header__button"
          >
            Регистрация
          </li>
          <li onClick={handleIsLogin} type="button" aria-label="войти" className="header__button">
            Войти
          </li>
        </ul>)  : (<Navigation />)}
      </div>
    </header>
  );
}
export default Header;
