import React from "react";
import illustration from "../../images/landing-logo.svg";
import '../Promo/Promo.css';

function Promo() {
  return (
    <section className="promo">
      <div className="promo__container">
      <h1 className="promo__title">Учебный проект студента факультета<br/>Веб-разработки.</h1>
      <p class="promo__subtitle">Листайте ниже, чтоы узнать больше про этот проект и его создателя.
      </p>
      <img class="promo__illustration" src={illustration} alt="планета"></img>
      <button type="button" aria-label ="узнать больше" class="promo__link">Узнать больше </button>
      </div>
    </section>
  );
}
export default Promo;
