import React, { useState } from 'react';
import { Link, useLocation } from "react-router-dom";
import '../BurgerMenu/BurgerMenu.css';
function BurgerMenu() {
    // const [isMenuOpen, setIsMenuOpen] = useState(false);
    const location = useLocation();

    // function handleMenuClick() {
    //   setIsMenuOpen(!isMenuOpen);
    // }

    // function addActiveClass(path) {
    //     return location.pathname === path ? 'burger-menu__link_active' : '';
    // }

    return (
      <>
        <button className="burger-menu__button"></button>
        <div className="burger-menu__container burger-menu__container_active">
          <button className="burger-menu__button-close" />
          <div className="burger-menu__content">
            <nav className="burger-menu__list">
              <Link to="/" className="burger-menu__item">Главная</Link>
              <Link to="/movies" className="burger-menu__item">Фильмы</Link>
              <Link to="/saved-movies" className="burger-menu__item">Сохранённые фильмы</Link>
            </nav>
            <Link to="/profile" className="burger-menu__profile">Аккаунт</Link>
          </div>
        </div>
      </>
    )
  }

  export default BurgerMenu;
