import React from "react";
import "../Profile/Profile.css";


function Profile() {
  return (
    <section className="profile">
      <div className="profile__container">
        <h1 className="profile__title">
          Привет, Олеся!
        </h1>
        <form
          className={"profile__form"}
          name="profile__form">
        <input
          className="profile__input-name"
          type="text"
          name="name"
          value={userName || ""}
          onChange={handleChangeUserName}
          placeholder="Ваше имя"
          required
          minLength={2}
          maxLength={40}></input>
        <span className="popup__input-error popup__input-error_type_name" />
        <input
          className="popup__input-text popup__input-subtitle-edit"
          type="email"
          name="email"
          value={userDescription || ""}
          onChange={handleChangeUserDescription}
          placeholder="Ваше хобби"
          required
          minLength={2}
          maxLength={200}
        />
        <span className="popup__input-error popup__input-error_type_about" />
          <button className="popup__button-save popup__button-save-add" 
          type="submit">
          {buttonText || "Сохранить"}
          </button>
        </form>
        <button type="button" aria-label="узнать больше" class="promo__link">
          Узнать больше{" "}
        </button>
      </div>
    </section>
  );
}
export default Profile;
