import React, { useState } from 'react';
import { Link, NavLink } from "react-router-dom";
import { useLocation } from "react-router-dom";
import '../BurgerMenu/BurgerMenu.css';
function BurgerMenu() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const location = useLocation();

    function handleMenuClick() {
      setIsMenuOpen(!isMenuOpen);
    }

    function addActiveClass(path) {
        return location.pathname === path ? '.burger-menu__item_active' : '';
    }

    return (
      <>
        <button className="burger-menu__button"></button>
        <div className="burger-menu__container">
          <button className="burger-menu__button-close" />
          <div className="burger-menu__content">
            <nav className="burger-menu__list">
              <NavLink to="/" className="burger-menu__item">Главная</NavLink>
              <NavLink to="/movies" className="burger-menu__item">Фильмы</NavLink>
              <NavLink to="/saved-movies" className="burger-menu__item">Сохранённые фильмы</NavLink>
            </nav>
            <NavLink to="/profile" className="burger-menu__profile">Аккаунт</NavLink>
          </div>
        </div>
      </>
    )
  }

  export default BurgerMenu;
